import React, { useState } from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LayoutMain from '../pages/layouts/LayoutMain'
import LayoutAdmin from '../pages/layouts/LayoutAdmin'
//Admin
import Dashboard from '../pages/views/Admin/Dashboard'
import ProductsManager from '../pages/views/Admin/Products'
import AddProduct from '../pages/views/Admin/AddProduct'
import dataFake from '../dataFake';

//Views
import About from '../pages/views/Main/About'
import Home from '../pages/views/Main/Home'
import Products from '../pages/views/Main/Products'
import DetailProduct from '../pages/views/Main/DetailProduct'


const Routers = ({ onRemove }) => {
    const [products, setProducts] = useState(dataFake);
    const onHandleRemove = (id) => {
        onRemove(id)
    }
    // const onHandleAdd = () => {
    //     onAdd(product)
    // }
    const onHandleAdd = (product) => {
        const newProduct = {
            id: products.length + 1,
            ...product
        }
        setProducts([
            ...products,
            newProduct
        ])
    }
    return (
        <Router>
            <Switch>
                <Route path="/admin/:path?" exact>
                    <LayoutAdmin>
                        <Switch>
                            <Route path='/admin' exact>
                                <Dashboard />
                            </Route>
                            <Route path='/admin/products'>
                                <ProductsManager products={products} onRemove={onHandleRemove} />
                            </Route>
                            <Route path='/admin/add-product'>
                                <AddProduct onAdd={onHandleAdd} />
                            </Route>
                        </Switch>
                    </LayoutAdmin>
                </Route>
                <Route>
                    <LayoutMain>
                        <Switch>
                            <Route path="/" exact>
                                <Home />
                            </Route>
                            <Route path="/collection">
                                <Products />
                            </Route>
                            <Route path="/product">
                                <DetailProduct />
                            </Route>
                            <Route path="/about">
                                <About />
                            </Route>
                        </Switch>
                    </LayoutMain>
                </Route>
            </Switch>
        </Router>
    )
}

Routers.propTypes = {

}

export default Routers
