import React, { useState } from 'react'
import { useForm } from 'react-hook-form'
import PropTypes from 'prop-types'

const AddProduct = ({ onAdd }) => {
    const [valueInput, setValue] = useState('');
    const { register, handleSubmit, errors } = useForm();
    const onChange = (e) => {
        const { name, value } = e.target;
        setValue({
            ...valueInput,
            [name]: value
        });
    }
    const onHandleSubmit = (e) => {
        e.preventDefault();
        onAdd(valueInput);

    }
    return (
        <div>
            <form onSubmit={handleSubmit(onHandleSubmit)}>
                <div className="form-group">
                    <label>Name</label>
                    <input
                        type="text" className="form-control" placeholder="Enter name"
                        name="name"
                        ref={register({ required: true })}
                        onChange={onChange} />
                    {errors.name && <p>Bạn chưa nhập tên</p>}
                </div>
                <div className="form-group">
                    <label>Image</label>
                    <input type="text" className="form-control" name="image" placeholder="Enter price" onChange={onChange} />
                </div>
                <div className="form-group">
                    <label>Price</label>
                    <input
                        type="text" className="form-control" placeholder="Enter price"
                        name="price"
                        ref={register({ required: true, pattern: /^\d+/ })}
                        onChange={onChange} />
                    {errors.price && <p>Bạn chưa nhập giá</p>}
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

AddProduct.propTypes = {

}

export default AddProduct
